cats = {
  try {
    return await FileAttachment("../_data/cats.json").json();
  } catch (e) {
    console.error("Error loading cats data. This is normal if you're running the book locally.", e);
    return await FileAttachment("../_dev/cats-fallback.json").json()
  }
